// WebRTCService.js - Real-time audio streaming with AudioWorkletNode
class WebRTCService {
  constructor() {
    this.listeners = new Set();
    this.isStreaming = false;
    this.mediaStream = null;
    this.audioContext = null;
    this.processor = null;
    this.signalingSocket = null;
    this.currentLanguage = 'en';
  }

  setLanguage(language) {
    this.currentLanguage = language;
  }

  addListener(callback) {
    this.listeners.add(callback);
    if (this.listeners.size === 1 && !this.isStreaming) this.startAudioStreaming();
  }

  removeListener(callback) {
    this.listeners.delete(callback);
    if (this.listeners.size === 0) this.stopAudioStreaming();
  }

  notifyListeners(message) {
    this.listeners.forEach(cb => { try { cb(message); } catch {} });
  }

  async startAudioStreaming() {
    if (this.isStreaming) return;
    try {
      this.notifyListeners({ type: 'status', data: 'Starting audio...' });
      this.mediaStream = await navigator.mediaDevices.getUserMedia({ audio: true });
      this.notifyListeners({ type: 'status', data: 'Microphone ready ‚úÖ' });
      await this.createWebSocketConnection();
      await this.setupAudioWorkletProcessing();
      this.isStreaming = true;
    } catch (error) {
      this.notifyListeners({ type: 'status', data: `Error: ${error.message} ‚ùå` });
    }
  }

  async createWebSocketConnection() {
    return new Promise((resolve, reject) => {
      const ws = new WebSocket('ws://localhost:8000/webrtc');
      ws.onopen = () => {
        this.signalingSocket = ws;
        this.notifyListeners({ type: 'status', data: 'Connected ‚úÖ' });
        ws.onmessage = (event) => {
          try {
            const data = JSON.parse(event.data);
            if (data.type === 'transcription') this.notifyListeners({ type: 'transcription', data: data.text });
            else if (data.type === 'status') this.notifyListeners({ type: 'status', data: data.message });
          } catch {}
        };
        resolve();
      };
      ws.onerror = (e) => { this.notifyListeners({ type: 'status', data: 'Connection error ‚ùå' }); reject(e); };
      ws.onclose = () => { this.signalingSocket = null; this.notifyListeners({ type: 'status', data: 'Disconnected ‚ùå' }); };
    });
  }

  async setupAudioWorkletProcessing() {
    this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const source = this.audioContext.createMediaStreamSource(this.mediaStream);
    // Load the AudioWorklet processor (audio-processor.js must be in public/)
    await this.audioContext.audioWorklet.addModule('/audio-processor.js');
    this.processor = new AudioWorkletNode(this.audioContext, 'audio-processor', {
      processorOptions: { chunkDurationSec: 3 } // tell the worklet to use 3s chunks
    });
    this.processor.port.onmessage = (event) => {
      if (event.data.type === 'audioData' && this.signalingSocket?.readyState === WebSocket.OPEN) {
        const pcmBuffer = event.data.data;
        this.signalingSocket.send(pcmBuffer);
      }
    };
    source.connect(this.processor);
    this.processor.connect(this.audioContext.destination);
    this.notifyListeners({ type: 'status', data: 'Recording... üî¥' });
  }

  stopAudioStreaming() {
    this.isStreaming = false;
    if (this.processor) { this.processor.disconnect(); this.processor = null; }
    if (this.audioContext) { this.audioContext.close(); this.audioContext = null; }
    if (this.signalingSocket) { this.signalingSocket.close(); this.signalingSocket = null; }
    if (this.mediaStream) { this.mediaStream.getTracks().forEach(t => t.stop()); this.mediaStream = null; }
    this.notifyListeners({ type: 'status', data: 'Stopped ‚èπÔ∏è' });
  }

  forceCleanup() {
    this.listeners.clear();
    this.stopAudioStreaming();
  }
}

const webRTCService = new WebRTCService();
if (typeof window !== 'undefined') window.webRTCService = webRTCService;
export default webRTCService;
