import React, { useState, useEffect, useRef } from 'react';

function TranscriptionDisplay({ transcription, allTranscriptions, connectionStatus }) {
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  // Debug logging
  console.log('ðŸŽ¯ TranscriptionDisplay rendered with:', { transcription, allTranscriptions, connectionStatus });

  // Function to determine status class for styling
  const getStatusClass = (status) => {
    if (status.includes('Connected') || status.includes('Recording')) {
      return 'status-connected';
    } else if (status.includes('Starting') || status.includes('Connecting')) {
      return 'status-connecting';
    } else if (status.includes('Error') || status.includes('Disconnected')) {
      return 'status-error';
    }
    return 'status-default';
  };

  const handleLanguageChange = async (event) => {
    const newLanguage = event.target.value;
    setSelectedLanguage(newLanguage);

    // Send the selected language to the backend
    try {
      const response = await fetch('http://localhost:8000/language', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ language: newLanguage }),
      });

      if (!response.ok) {
        console.error('Failed to update language');
      }
    } catch (error) {
      console.error('Error updating language:', error);
    }
  };

  return (
    <div className="transcription-absolute-table">
      <div className="table-row">
        <div className="table-cell" style={{ gridColumn: '1 / span 2' }}>
          <div className="control-bar">
            <div className="language-selector">
              <select className='language-dropdown' value={selectedLanguage} disabled>
                <option value="en">English</option>
                <option value="yue">Cantonese</option>
                <option value="de">German</option>
                <option value="zh">Mandarin</option>
              </select>
              <span style={{ marginLeft: 8, color: '#888', fontSize: 12 }}>
                More Languages Coming Soon!
              </span>
            </div>
            <div className="connection-status">
              <div className={`status-indicator ${getStatusClass(connectionStatus)}`}>
                <div className="status-dot"></div>
                <span className="status-text">{connectionStatus}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="table-row">
        <div className="table-cell transcription-cell">
          <div className="transcription-display">
            <p className="live-text">
              {transcription || 'Waiting for speech...'}
            </p>
            <div style={{ fontSize: '12px', color: '#666', marginTop: '10px' }}>
              Debug: "{transcription}" | Length: {transcription?.length || 0}
            </div>
          </div>
        </div>
        <div className="table-cell history-cell">
          <div className="history-display">
            <div className="history-log">
              {allTranscriptions.length === 0 ? (
                <p style={{ color: '#888', fontStyle: 'italic' }}>
                  History currently unavailable
                </p>
              ) : (
                allTranscriptions.map((text, index) => (
                  <p key={index}>{text}</p>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TranscriptionDisplay;
