import React from 'react';
import ImageDisplay from '../ImageDisplay/ImageDisplay';

function TranscriptionHistory({ allTranscriptions, imageUrls, fullScreen, onToggleFullScreen }) {
  // Provide a default no-op function if onToggleFullScreen is not passed
  const toggleFullScreen = typeof onToggleFullScreen === 'function' ? onToggleFullScreen : () => {};

  const uniqueTranscriptions = Array.from(new Set(allTranscriptions));

  // Prevent click from bubbling to parent elements
  const handleButtonClick = (e) => {
    e.stopPropagation();
    toggleFullScreen();
  };

  return (
    <div className="table-cell history-cell">
      <div className="history-display">
        <div className={fullScreen ? 'fullscreen-history-log glassy-fade' : 'history-log'}>
          <button
            onClick={handleButtonClick}
            className="fullscreen-toggle-btn"
            aria-label={fullScreen ? 'Exit Full Screen' : 'Full Screen'}
            type="button"
          >
            {fullScreen ? (
              <span>&#x2715; Exit</span>
            ) : (
              <span>&#x26F6; Full Screen</span>
            )}
          </button>
          {uniqueTranscriptions.length === 0 ? (
            <p style={{ color: '#888', fontStyle: 'italic' }}>
              History currently unavailable
            </p>
          ) : (
            uniqueTranscriptions.map((text, index) => {
              const images = imageUrls[index];
              return (
                <div key={index} className="history-item">
                  <p>{text}</p>
                  {images && <ImageDisplay images={images} />}
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

export default TranscriptionHistory;
