import React, { useState, useEffect, useRef } from 'react';
import './styles.css';
import TranscriptionDisplay from './TranscriptionDisplay';

function App() {
  const [transcription, setTranscription] = useState('');
  const [allTranscriptions, setAllTranscriptions] = useState([]);
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  const ws = useRef(null); // holds WebSocket instance
  const connected = useRef(false); // tracks connection state
  const retryInterval = useRef(1000); // initial retry interval (ms)

  useEffect(() => {
    let retryTimeout;

    const connectWebSocket = () => {
      if (connected.current) {
        console.log('WebSocket connection already active.');
        return;
      }

      console.log('Attempting to connect to WebSocket...');

      try {
        ws.current = new WebSocket('ws://localhost:8000/ws');

        ws.current.onopen = () => {
          console.log('✅ WebSocket connected successfully');
          connected.current = true;
          setConnectionStatus('Connected ✅');
          retryInterval.current = 1000; // reset retry interval on successful connect
        };

        ws.current.onmessage = (event) => {
          const newTranscription = event.data;
          console.log('📥 Received transcription:', newTranscription);
          setTranscription(newTranscription);
          setAllTranscriptions(prev => [newTranscription, ...prev]);
        };

        ws.current.onclose = (event) => {
          console.log('❌ WebSocket disconnected. Code:', event.code, 'Reason:', event.reason);
          connected.current = false;
          setConnectionStatus('Disconnected ❌');
          retryTimeout = setTimeout(() => {
            console.log('🔄 Retrying WebSocket connection...');
            setConnectionStatus('Reconnecting...');
            connectWebSocket();
          }, retryInterval.current);
          retryInterval.current = Math.min(retryInterval.current * 2, 30000); // cap at 30s
        };

        ws.current.onerror = (error) => {
          console.error('❌ WebSocket error:', error);
          connected.current = false;
          setConnectionStatus('Error ❌');
          if (ws.current) {
            ws.current.close();
          }
        };
      } catch (error) {
        console.error('❌ Failed to create WebSocket connection:', error);
        connected.current = false;
        setConnectionStatus('Failed ❌');
        retryTimeout = setTimeout(connectWebSocket, retryInterval.current);
      }
    };

    connectWebSocket();

    return () => {
      if (retryTimeout) clearTimeout(retryTimeout);
      if (ws.current) {
        ws.current.onopen = null;
        ws.current.onmessage = null;
        ws.current.onclose = null;
        ws.current.onerror = null;
        ws.current.close();
      }
      connected.current = false;
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <div style={{ position: 'absolute', top: '100px', right: '20px', fontSize: '14px', padding: '5px 10px', background: 'rgba(0,0,0,0.1)', borderRadius: '5px' }}>
          WebSocket: {connectionStatus}
        </div>
        <TranscriptionDisplay
          transcription={transcription}
          allTranscriptions={allTranscriptions}
        />
      </header>
    </div>
  );
}

export default App;
