import React, { useState, useEffect } from 'react';
import './styles.css';
import TranscriptionDisplay from './TranscriptionDisplay';
import webSocketService from './WebSocketService';

function App() {
  const [transcription, setTranscription] = useState('');
  const [allTranscriptions, setAllTranscriptions] = useState([]);
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  useEffect(() => {
    console.log('ðŸŽ¯ App component mounted, setting up WebSocket listener');

    const handleWebSocketMessage = (message) => {
      if (message.type === 'status') {
        setConnectionStatus(message.data);
      } else if (message.type === 'transcription') {
        setTranscription(message.data);
        setAllTranscriptions(prev => [message.data, ...prev]);
      }
    };

    // Add listener and connect
    webSocketService.addListener(handleWebSocketMessage);
    webSocketService.connect();

    return () => {
      console.log('ðŸ§¹ App component unmounting, removing WebSocket listener');
      webSocketService.removeListener(handleWebSocketMessage);
      // Don't disconnect here - let other components use the same connection
    };
  }, []); // Empty dependency array - only run once

  return (
    <div className="App">
      <header className="App-header">
        <div style={{ position: 'absolute', top: '100px', right: '20px', fontSize: '14px', padding: '5px 10px', background: 'rgba(0,0,0,0.1)', borderRadius: '5px' }}>
          WebSocket: {connectionStatus}
        </div>
        <TranscriptionDisplay
          transcription={transcription}
          allTranscriptions={allTranscriptions}
        />
      </header>
    </div>
  );
}

export default App;
