import React, { useState, useEffect, useRef } from 'react';
import './styles.css';
import TranscriptionDisplay from './TranscriptionDisplay';

function App() {
  const [transcription, setTranscription] = useState('');
  const [allTranscriptions, setAllTranscriptions] = useState([]);
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  const ws = useRef(null); // holds WebSocket instance
  const connected = useRef(false); // tracks connection state
  const retryInterval = useRef(1000); // initial retry interval (ms)

  useEffect(() => {
    let retryTimeout;
    let isComponentMounted = true;

    const connectWebSocket = () => {
      // Don't create new connection if already connected or component unmounted
      if (connected.current || !isComponentMounted) {
        console.log('WebSocket connection already active or component unmounted.');
        return;
      }

      // Clean up any existing connection first
      if (ws.current) {
        ws.current.close();
        ws.current = null;
      }

      console.log('Attempting to connect to WebSocket...');

      try {
        ws.current = new WebSocket('ws://localhost:8000/ws');

        ws.current.onopen = () => {
          if (!isComponentMounted) return;
          console.log('âœ… WebSocket connected successfully');
          connected.current = true;
          setConnectionStatus('Connected âœ…');
          retryInterval.current = 1000; // reset retry interval on successful connect
        };

        ws.current.onmessage = (event) => {
          if (!isComponentMounted) return;
          const newTranscription = event.data;
          console.log('ðŸ“¥ Received transcription:', newTranscription);
          setTranscription(newTranscription);
          setAllTranscriptions(prev => [newTranscription, ...prev]);
        };

        ws.current.onclose = (event) => {
          if (!isComponentMounted) return;
          console.log('âŒ WebSocket disconnected. Code:', event.code, 'Reason:', event.reason);
          connected.current = false;
          setConnectionStatus('Disconnected âŒ');

          // Only retry if the component is still mounted and we want to reconnect
          if (isComponentMounted && event.code !== 1000) { // 1000 is normal closure
            retryTimeout = setTimeout(() => {
              if (isComponentMounted) {
                console.log('ðŸ”„ Retrying WebSocket connection...');
                setConnectionStatus('Reconnecting...');
                connectWebSocket();
              }
            }, retryInterval.current);
            retryInterval.current = Math.min(retryInterval.current * 2, 30000); // cap at 30s
          }
        };

        ws.current.onerror = (error) => {
          if (!isComponentMounted) return;
          console.error('âŒ WebSocket error:', error);
          connected.current = false;
          setConnectionStatus('Error âŒ');
        };
      } catch (error) {
        if (!isComponentMounted) return;
        console.error('âŒ Failed to create WebSocket connection:', error);
        connected.current = false;
        setConnectionStatus('Failed âŒ');
        retryTimeout = setTimeout(() => {
          if (isComponentMounted) {
            connectWebSocket();
          }
        }, retryInterval.current);
      }
    };

    connectWebSocket();

    return () => {
      console.log('ðŸ§¹ Cleaning up WebSocket connection...');
      isComponentMounted = false;

      if (retryTimeout) {
        clearTimeout(retryTimeout);
      }

      if (ws.current) {
        connected.current = false;
        ws.current.onopen = null;
        ws.current.onmessage = null;
        ws.current.onclose = null;
        ws.current.onerror = null;
        ws.current.close();
        ws.current = null;
      }
    };
  }, []); // Empty dependency array - only run once

  return (
    <div className="App">
      <header className="App-header">
        <div style={{ position: 'absolute', top: '100px', right: '20px', fontSize: '14px', padding: '5px 10px', background: 'rgba(0,0,0,0.1)', borderRadius: '5px' }}>
          WebSocket: {connectionStatus}
        </div>
        <TranscriptionDisplay
          transcription={transcription}
          allTranscriptions={allTranscriptions}
        />
      </header>
    </div>
  );
}

export default App;
