import React, { useEffect, useState } from 'react';

function WebSearchDisplay({ taskSummary }) {
  const [searchResult, setSearchResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [ask, setAsk] = useState(true);

  useEffect(() => {
    setAsk(true);
    setSearchResult('');
    setError(null);
  }, [taskSummary]);

  const handleSearch = () => {
    setLoading(true);
    setError(null);
    fetch('http://localhost:8000/web-search', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ query: taskSummary })
    })
      .then(res => res.json())
      .then(data => {
        let answer = data.answer || 'No answer found.';
        setSearchResult(answer);
        setLoading(false);
      })
      .catch(err => {
        setError('Web search failed.');
        setLoading(false);
      });
  };

  if (!taskSummary) return null;

  return (
    <div className="websearch-glass websearch">
      <div className="websearch-title">Web Search</div>
      {ask ? (
        <div style={{ margin: '1em 0' }}>
          <div>Would you like to search Google for:</div>
          <div style={{ fontStyle: 'italic', margin: '0.5em 0' }}>{taskSummary}</div>
          <button onClick={() => { setAsk(false); handleSearch(); }} style={{ marginRight: 8 }}>Yes, search</button>
          <button onClick={() => setAsk(false)}>No</button>
        </div>
      ) : loading ? (
        <div className="websearch-loading">Searching...</div>
      ) : error ? (
        <div className="websearch-error">{error}</div>
      ) : (
        <div className="websearch-result">{searchResult}</div>
      )}
    </div>
  );
}

export default WebSearchDisplay;
