import React, { useState, useEffect } from 'react';
import './styles.css';
import TranscriptionDisplay from './TranscriptionDisplay'; // Assuming you have a TranscriptionDisplay component

function App() {
  const [transcription, setTranscription] = useState('');
  const [allTranscriptions, setAllTranscriptions] = useState([]);

  useEffect(() => {
    let ws;
    let retryInterval = 1000; // Start with 1 second

    const connectWebSocket = () => {
      ws = new WebSocket('ws://localhost:8000/ws');

      ws.onopen = () => {
        console.log('WebSocket connected');
        retryInterval = 1000; // Reset retry interval on successful connection
      };

      ws.onmessage = (event) => {
        const newTranscription = event.data;
        console.log('Received:', newTranscription);
        setTranscription(newTranscription); // Show the latest transcription
        setAllTranscriptions(prev => [newTranscription, ...prev]); // Add to history
      };

      ws.onclose = () => {
        console.log('WebSocket disconnected, retrying...');
        setTimeout(connectWebSocket, retryInterval);
        retryInterval = Math.min(retryInterval * 2, 30000); // Exponential backoff, max 30 seconds
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        ws.close(); // Ensure the connection is closed on error
      };
    };

    connectWebSocket();

    return () => {
      if (ws && ws.readyState === WebSocket.OPEN) {
        ws.close();
      }
    };
  }, []); // The empty array ensures this effect runs only once

  return (
    <div className="App">
      <header className="App-header">
        <TranscriptionDisplay 
          transcription={transcription} 
          allTranscriptions={allTranscriptions} 
        />
      </header>
    </div>
  );
}

export default App;
