import React, { useEffect } from 'react';
import { Outlet, Link } from 'react-router-dom';
import logoLight from './logo_lightmode.svg';
import logoDark from './logo_darkmode.svg';
import './Landing.css'; // Import the styles

const Layout = () => {
  useEffect(() => {
    // Add the fade-in class to the body
    document.body.classList.add('fade-out');

    // Remove the fade-in class after the transition completes
    setTimeout(() => {
      document.body.classList.remove('fade-in');
    }, 2000); // Match the duration of the fade-in effect (2 seconds)

    // Clean up the transition-active class after navigation
    document.body.addEventListener('transitionend', () => {
      document.body.classList.remove('transition-active');
    });
  }, []);

  return (
    <>
      <header className="site-header">
        <Link to="/" className="logo-link">
          <picture>
            <source srcSet={logoDark} media="(prefers-color-scheme: dark)" />
            <img src={logoLight} className="App-logo" alt="logo" />
          </picture>
        </Link>
      </header>
      <main>
        <Outlet /> {/* Render child routes here */}
      </main>
    </>
  );
};

export default Layout;
