import React, { useEffect, useState } from 'react';

function TaskDisplay() {
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function getWeather() {
      setLoading(true);
      setError(null);
      try {
        const res = await fetch('http://localhost:8000/gemma-python', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ prompt: 'how is the weather today?' })
        });
        if (!res.ok) throw new Error('Failed to fetch Gemma response');
        const data = await res.json();
        setResponse(data.response || 'No response');
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    getWeather();
  }, []);

  if (loading) return <div>Loading weather from Gemma...</div>;
  if (error) return <div style={{ color: 'red' }}>Error: {error}</div>;
  return <div><strong>Gemma says:</strong> {response}</div>;
}

export default TaskDisplay;
