import React, { useState } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import logoLight from './logo_lightmode.svg';
import logoDark from './logo_darkmode.svg';

const Layout = () => {
  const [fade, setFade] = useState(false);
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    setFade(true);
    setTimeout(() => {
      navigate(path);
      setFade(false);
    }, 300); // Match this duration to the CSS transition time
  };

  return (
    <div className={`fade-container ${fade ? 'fade-out' : 'fade-in'}`}>
      <header className="site-header">
        <button onClick={() => handleNavigation('/')} className="logo-link">
          <picture>
            <source srcSet={logoDark} media="(prefers-color-scheme: dark)" />
            <img src={logoLight} className="App-logo" alt="logo" />
          </picture>
        </button>
      </header>
      <main>
        <Outlet />
      </main>
    </div>
  );
};

export default Layout;
