import React, { useState } from 'react';
import ImageDisplay from '../ImageDisplay/ImageDisplay';

// allTranscriptions should now be an array of { text, timestamp, images }
function TranscriptionHistory({ allTranscriptions, setAllTranscriptions }) {
  const [showHistory, setShowHistory] = useState(false);

  function exportHistory(history) {
    const dataStr = JSON.stringify(history, null, 2);
    const blob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'history.json';
    a.click();
    URL.revokeObjectURL(url);
  }

  function handleCleanLogs() {
    // Example cleaning: trim whitespace and remove empty logs
    const cleaned = allTranscriptions
      .map(item => ({ ...item, text: item.text.trim() }))
      .filter(item => item.text.length > 0);
    setAllTranscriptions(cleaned);
  }

  return (
    <div className="table-cell history-cell">
      <button onClick={() => setShowHistory((prev) => !prev)}>
        {showHistory ? 'Hide Logs' : 'Show Logs'}
      </button>
      {showHistory && (
        <div className="history-display">
          <div className="history-log">
            {allTranscriptions.length === 0 ? (
              <p style={{ color: '#888', fontStyle: 'italic' }}>
                History currently unavailable
              </p>
            ) : (
              allTranscriptions.map((item, index) => (
                <div key={index} className="history-item">
                  <p>
                    <span style={{ color: '#aaa', fontSize: '0.85em', marginRight: 8 }}>
                      {new Date(item.timestamp).toLocaleTimeString()}
                    </span>
                    {item.text}
                  </p>
                  {item.images && <ImageDisplay images={item.images} />}
                </div>
              ))
            )}
          </div>
        </div>
      )}
      <button onClick={handleCleanLogs}>
        Clean Logs
      </button>
      <button onClick={() => exportHistory(allTranscriptions)}>
        Export History
      </button>
    </div>
  );
}

export default TranscriptionHistory;
