import React, { useEffect } from 'react';
import './Landing.css'; // Import the styles

function Landing() {
  useEffect(() => {
    // Add the fade-in class to the body
    document.body.classList.add('fade-in');

    // Remove the fade-in class after the transition completes
    setTimeout(() => {
      document.body.classList.remove('fade-in');
    }, 2000); // Match the duration of the fade-in effect (2 seconds)

    // Clean up the transition-active class after navigation
    document.body.addEventListener('transitionend', () => {
      document.body.classList.remove('transition-active');
    });
  }, []);

  return (
    <div className="landing-body">
      <div className="content-overlay">
        <h1 className="text-5xl font-bold mb-4">Speak Now</h1>
        <p className="text-xl text-gray-300">Real-time Transcription with AI</p>
        <a
          href="/transcribe"
          className="start-button"
          onClick={(e) => {
            e.preventDefault(); // Prevent immediate navigation
            document.body.classList.add('transition-active');
            setTimeout(() => {
              window.location.href = '/transcribe'; // Navigate after the transition
            }, 2000); // Match the duration of the transition
          }}
        >
          Start Transcribing
        </a>       
      </div>

      <div className="arc-container">
        <div className="arc-gradient"></div>
      </div>
    </div>
  );
}

export default Landing;
