import React, { useState, useEffect } from 'react';
import './styles.css';
import TranscriptionDisplay from './TranscriptionDisplay'; // Assuming you have a TranscriptionDisplay component

function App() {
  const [transcription, setTranscription] = useState('');
  const [allTranscriptions, setAllTranscriptions] = useState([]);

  useEffect(() => {
    let ws;

    const connectWebSocket = () => {
      ws = new WebSocket('ws://localhost:8000/ws');

      ws.onopen = () => {
        console.log('WebSocket connected');
      };

      ws.onmessage = (event) => {
        const newTranscription = event.data;
        console.log('Received:', newTranscription);
        setTranscription(newTranscription); // Show the latest transcription
        setAllTranscriptions(prev => [newTranscription, ...prev]); // Add to the list
      };

      ws.onclose = () => {
        console.log('WebSocket disconnected. Reconnecting in 2 seconds...');
        setTimeout(() => connectWebSocket(), 1000); // Retry connection after 2 seconds
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
      };
    };

    connectWebSocket();

    // Add fade-out effect when the component unmounts
    return () => {
      document.body.classList.add('transition-active');
      setTimeout(() => {
        if (ws && ws.readyState === WebSocket.OPEN) {
          ws.close();
        }
      }, 2000); // Match the duration of the fade-out effect
    };
  }, []); // The empty array ensures this effect runs only once

  return (
    <div className="App">
      <header className="App-header">
        <TranscriptionDisplay 
          transcription={transcription} 
          allTranscriptions={allTranscriptions} 
        />
      </header>
    </div>
  );
}

export default App;
